import { Link, useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";
import {
  Form,
  FormControl,
  FormDescription,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { UserPlus, CheckCircle2 } from "lucide-react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { useMutation } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

const registerSchema = z.object({
  name: z.string().min(1, "Nome √© obrigat√≥rio"),
  email: z.string().email("Email inv√°lido"),
  password: z
    .string()
    .min(8, "A senha deve ter no m√≠nimo 8 caracteres")
    .regex(/[a-z]/, "A senha deve conter pelo menos uma letra min√∫scula")
    .regex(/[A-Z]/, "A senha deve conter pelo menos uma letra mai√∫scula")
    .regex(/[0-9]/, "A senha deve conter pelo menos um n√∫mero"),
  confirmPassword: z.string(),
}).refine(data => data.password === data.confirmPassword, {
  message: "As senhas n√£o coincidem",
  path: ["confirmPassword"],
});

type RegisterFormData = z.infer<typeof registerSchema>;

export default function CreateAccount() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();

  const form = useForm<RegisterFormData>({
    resolver: zodResolver(registerSchema),
    defaultValues: {
      name: "",
      email: "",
      password: "",
      confirmPassword: "",
    },
  });

  const registerMutation = useMutation({
    mutationFn: async (data: RegisterFormData) => {
      return await apiRequest("POST", "/api/auth/register", data);
    },
    onSuccess: () => {
      toast({
        title: "Conta criada com sucesso!",
        description: "Voc√™ j√° est√° logado e pode come√ßar a usar o sistema.",
      });
      setLocation("/home");
    },
    onError: (error: any) => {
      toast({
        variant: "destructive",
        title: "Erro ao criar conta",
        description: error.message || "Ocorreu um erro. Tente novamente.",
      });
    },
  });

  const onSubmit = (data: RegisterFormData) => {
    registerMutation.mutate(data);
  };

  const password = form.watch("password");
  const hasMinLength = password?.length >= 8;
  const hasLowercase = /[a-z]/.test(password || "");
  const hasUppercase = /[A-Z]/.test(password || "");
  const hasNumber = /[0-9]/.test(password || "");

  return (
    <div className="min-h-screen flex items-center justify-center bg-background p-4">
      <Card className="w-full max-w-md">
        <CardHeader className="space-y-4 text-center">
          <div className="flex justify-center">
            <div className="rounded-full bg-primary/10 p-4">
              <UserPlus className="h-8 w-8 text-primary" />
            </div>
          </div>
          <CardTitle className="text-2xl font-bold">
            Criar Nova Conta
          </CardTitle>
          <CardDescription>
            Preencha os dados abaixo para criar sua conta no SUS Para Todos
          </CardDescription>
        </CardHeader>
        <CardContent>
          <Form {...form}>
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
              <FormField
                control={form.control}
                name="name"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Nome Completo</FormLabel>
                    <FormControl>
                      <Input
                        {...field}
                        placeholder="Jo√£o da Silva"
                        data-testid="input-name"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="email"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Email</FormLabel>
                    <FormControl>
                      <Input
                        {...field}
                        type="email"
                        placeholder="joao@exemplo.com"
                        data-testid="input-email"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="password"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Senha</FormLabel>
                    <FormControl>
                      <Input
                        {...field}
                        type="password"
                        placeholder="Sua senha"
                        data-testid="input-password"
                      />
                    </FormControl>
                    <FormDescription>
                      <div className="space-y-1 mt-2">
                        <div className={`flex items-center gap-2 text-xs ${hasMinLength ? 'text-green-600 dark:text-green-400' : 'text-muted-foreground'}`}>
                          <CheckCircle2 className={`h-3 w-3 ${hasMinLength ? 'opacity-100' : 'opacity-30'}`} />
                          <span>M√≠nimo 8 caracteres</span>
                        </div>
                        <div className={`flex items-center gap-2 text-xs ${hasLowercase ? 'text-green-600 dark:text-green-400' : 'text-muted-foreground'}`}>
                          <CheckCircle2 className={`h-3 w-3 ${hasLowercase ? 'opacity-100' : 'opacity-30'}`} />
                          <span>Uma letra min√∫scula</span>
                        </div>
                        <div className={`flex items-center gap-2 text-xs ${hasUppercase ? 'text-green-600 dark:text-green-400' : 'text-muted-foreground'}`}>
                          <CheckCircle2 className={`h-3 w-3 ${hasUppercase ? 'opacity-100' : 'opacity-30'}`} />
                          <span>Uma letra mai√∫scula</span>
                        </div>
                        <div className={`flex items-center gap-2 text-xs ${hasNumber ? 'text-green-600 dark:text-green-400' : 'text-muted-foreground'}`}>
                          <CheckCircle2 className={`h-3 w-3 ${hasNumber ? 'opacity-100' : 'opacity-30'}`} />
                          <span>Um n√∫mero</span>
                        </div>
                      </div>
                    </FormDescription>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="confirmPassword"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Confirmar Senha</FormLabel>
                    <FormControl>
                      <Input
                        {...field}
                        type="password"
                        placeholder="Confirme sua senha"
                        data-testid="input-confirm-password"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <Button
                type="submit"
                className="w-full"
                disabled={registerMutation.isPending}
                data-testid="button-submit"
              >
                {registerMutation.isPending ? "Criando conta..." : "Criar Conta"}
              </Button>
            </form>
          </Form>

          <div className="mt-4 text-center text-sm">
            <span className="text-muted-foreground">J√° tem uma conta? </span>
            <Link href="/login">
              <a className="text-primary hover:underline" data-testid="link-login">
                Fazer login
              </a>
            </Link>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/ForgotPassword.tsx.content:
import { useState, useMemo } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Link, useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import {
  Form,
  FormControl,
  FormDescription,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import {
  Card,
  CardContent,
  CardDescription,
  CardFooter,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";
import { useToast } from "@/hooks/use-toast";
import { useMutation } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { Check } from "lucide-react";

const verifyEmailSchema = z.object({
  email: z.string().email("E-mail inv√°lido"),
});

const resetPasswordSchema = z.object({
  newPassword: z.string().min(8, "A senha deve ter no m√≠nimo 8 caracteres"),
  confirmPassword: z.string().min(1, "Confirma√ß√£o de senha √© obrigat√≥ria"),
}).refine((data) => data.newPassword === data.confirmPassword, {
  message: "As senhas n√£o coincidem",
  path: ["confirmPassword"],
});

type VerifyEmailData = z.infer<typeof verifyEmailSchema>;
type ResetPasswordData = z.infer<typeof resetPasswordSchema>;

export default function ForgotPassword() {
  const { toast } = useToast();
  const [, setLocation] = useLocation();
  const [step, setStep] = useState<"email" | "password">("email");
  const [userId, setUserId] = useState<string>("");
  const [userEmail, setUserEmail] = useState<string>("");

  const emailForm = useForm<VerifyEmailData>({
    resolver: zodResolver(verifyEmailSchema),
    defaultValues: {
      email: "",
    },
  });

  const passwordForm = useForm<ResetPasswordData>({
    resolver: zodResolver(resetPasswordSchema),
    defaultValues: {
      newPassword: "",
      confirmPassword: "",
    },
  });

  const verifyEmailMutation = useMutation({
    mutationFn: async (data: VerifyEmailData) => {
      const res = await apiRequest("POST", "/api/auth/verify-email", data);
      return await res.json();
    },
    onSuccess: (response: any) => {
      setUserId(response.userId);
      setUserEmail(emailForm.getValues("email"));
      setStep("password");
      toast({
        title: "Email verificado!",
        description: "Agora voc√™ pode redefinir sua senha.",
      });
    },
    onError: (error: any) => {
      toast({
        variant: "destructive",
        title: "Email n√£o encontrado",
        description: error.message || "Este email n√£o est√° cadastrado no sistema.",
      });
    },
  });

  const resetPasswordMutation = useMutation({
    mutationFn: async (data: ResetPasswordData) => {
      if (!userId) {
        throw new Error("ID do usu√°rio n√£o encontrado. Por favor, verifique o email novamente.");
      }
      const res = await apiRequest("POST", "/api/auth/reset-password", {
        userId,
        newPassword: data.newPassword,
        confirmPassword: data.confirmPassword,
      });
      return await res.json();
    },
    onSuccess: () => {
      toast({
        title: "Senha redefinida!",
        description: "Sua senha foi alterada com sucesso. Fa√ßa login com a nova senha.",
      });
      setLocation("/login");
    },
    onError: (error: any) => {
      toast({
        variant: "destructive",
        title: "Erro ao redefinir senha",
        description: error.message || "Tente novamente mais tarde.",
      });
    },
  });

  const onVerifyEmail = (data: VerifyEmailData) => {
    verifyEmailMutation.mutate(data);
  };

  const onResetPassword = (data: ResetPasswordData) => {
    resetPasswordMutation.mutate(data);
  };

  const newPassword = passwordForm.watch("newPassword") || "";
  const passwordRequirements = useMemo(() => ({
    minLength: newPassword.length >= 8,
    hasUpperCase: /[A-Z]/.test(newPassword),
    hasLowerCase: /[a-z]/.test(newPassword),
    hasNumber: /[0-9]/.test(newPassword),
  }), [newPassword]);

  return (
    <div className="min-h-screen flex items-center justify-center bg-background p-4">
      <Card className="w-full max-w-md">
        <CardHeader className="space-y-2">
          <CardTitle className="text-2xl font-bold text-center">
            {step === "email" ? "Recuperar Senha" : "Redefinir Senha"}
          </CardTitle>
          <CardDescription className="text-center">
            {step === "email" 
              ? "Digite seu e-mail para recuperar sua senha"
              : `Digite sua nova senha para ${userEmail}`
            }
          </CardDescription>
        </CardHeader>
        <CardContent>
          {step === "email" ? (
            <Form {...emailForm}>
              <form onSubmit={emailForm.handleSubmit(onVerifyEmail)} className="space-y-6">
                <FormField
                  control={emailForm.control}
                  name="email"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>E-mail</FormLabel>
                      <FormControl>
                        <Input
                          {...field}
                          type="email"
                          placeholder="seu@email.com"
                          data-testid="input-email"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <Button
                  type="submit"
                  className="w-full"
                  disabled={verifyEmailMutation.isPending}
                  data-testid="button-verify-email"
                >
                  {verifyEmailMutation.isPending ? "Verificando..." : "Verificar Email"}
                </Button>
              </form>
            </Form>
          ) : (
            <Form {...passwordForm} key="password-form">
              <form onSubmit={passwordForm.handleSubmit(onResetPassword)} className="space-y-4">
                    <FormField
                      control={passwordForm.control}
                      name="newPassword"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Nova Senha</FormLabel>
                          <FormControl>
                            <Input
                              {...field}
                              type="password"
                              placeholder="Digite sua nova senha"
                              data-testid="input-new-password"
                            />
                          </FormControl>
                          <FormDescription>
                            <div className="space-y-1 mt-2">
                              <div className={`flex items-center gap-2 text-xs ${passwordRequirements.minLength ? 'text-green-600 dark:text-green-400' : 'text-muted-foreground'}`}>
                                <Check className={`h-3 w-3 ${passwordRequirements.minLength ? 'opacity-100' : 'opacity-30'}`} data-testid="indicator-min-length" />
                                <span>M√≠nimo de 8 caracteres</span>
                              </div>
                              <div className={`flex items-center gap-2 text-xs ${passwordRequirements.hasUpperCase ? 'text-green-600 dark:text-green-400' : 'text-muted-foreground'}`}>
                                <Check className={`h-3 w-3 ${passwordRequirements.hasUpperCase ? 'opacity-100' : 'opacity-30'}`} data-testid="indicator-uppercase" />
                                <span>Pelo menos uma letra mai√∫scula</span>
                              </div>
                              <div className={`flex items-center gap-2 text-xs ${passwordRequirements.hasLowerCase ? 'text-green-600 dark:text-green-400' : 'text-muted-foreground'}`}>
                                <Check className={`h-3 w-3 ${passwordRequirements.hasLowerCase ? 'opacity-100' : 'opacity-30'}`} data-testid="indicator-lowercase" />
                                <span>Pelo menos uma letra min√∫scula</span>
                              </div>
                              <div className={`flex items-center gap-2 text-xs ${passwordRequirements.hasNumber ? 'text-green-600 dark:text-green-400' : 'text-muted-foreground'}`}>
                                <Check className={`h-3 w-3 ${passwordRequirements.hasNumber ? 'opacity-100' : 'opacity-30'}`} data-testid="indicator-number" />
                                <span>Pelo menos um n√∫mero</span>
                              </div>
                            </div>
                          </FormDescription>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                <FormField
                  control={passwordForm.control}
                  name="confirmPassword"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Confirmar Nova Senha</FormLabel>
                      <FormControl>
                        <Input
                          {...field}
                          type="password"
                          placeholder="Digite novamente sua nova senha"
                          data-testid="input-confirm-password"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <Button
                  type="submit"
                  className="w-full"
                  disabled={resetPasswordMutation.isPending}
                  data-testid="button-reset-password"
                >
                  {resetPasswordMutation.isPending ? "Redefinindo..." : "Redefinir Senha"}
                </Button>
              </form>
            </Form>
          )}
        </CardContent>
        <CardFooter className="flex justify-center">
          <p className="text-sm text-muted-foreground">
            Lembrou sua senha?{" "}
            <Link 
              href="/login"
              className="text-primary font-medium hover:underline"
              data-testid="link-login"
            >
              Voltar para login
            </Link>
          </p>
        </CardFooter>
      </Card>
    </div>
  );
}

